import React, { useState, useRef } from 'react';

const RecordScreen = () => {
  const [recording, setRecording] = useState(false);
  const [recordedVideo, setRecordedVideo] = useState(null);
  const [recordingTime, setRecordingTime] = useState(180);
  const [includeAudio, setIncludeAudio] = useState(true);
  const [timer, setTimer] = useState(0);
  const [progress, setProgress] = useState(0);
  
  const mediaRecorderRef = useRef(null);
  const videoRef = useRef(null);
  const timerRef = useRef(null);

  const startRecording = async () => {
    try {
      // Get screen stream
      const screenStream = await navigator.mediaDevices.getDisplayMedia({
        video: { cursor: "always" },
        audio: includeAudio
      });
      
      // Get microphone audio if enabled
      let audioStream = null;
      if (includeAudio) {
        audioStream = await navigator.mediaDevices.getUserMedia({
          audio: true,
          video: false
        });
      }
      
      // Combine streams
      const combinedStream = new MediaStream();
      screenStream.getVideoTracks().forEach(track => combinedStream.addTrack(track));
      
      if (audioStream) {
        audioStream.getAudioTracks().forEach(track => combinedStream.addTrack(track));
      }
      
      // Setup media recorder
      mediaRecorderRef.current = new MediaRecorder(combinedStream, {
        mimeType: 'video/webm; codecs=vp9'
      });
      
      const chunks = [];
      mediaRecorderRef.current.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunks.push(e.data);
        }
      };
      
      mediaRecorderRef.current.onstop = () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        const videoUrl = URL.createObjectURL(blob);
        setRecordedVideo({ blob, url: videoUrl });
        
        // Reset timer and progress
        clearInterval(timerRef.current);
        setTimer(0);
        setProgress(0);
      };
      
      // Start recording
      mediaRecorderRef.current.start();
      setRecording(true);
      
      // Start timer
      let timeElapsed = 0;
      timerRef.current = setInterval(() => {
        timeElapsed += 1;
        setTimer(timeElapsed);
        
        // Calculate progress percentage
        const progressValue = (timeElapsed / recordingTime) * 100;
        setProgress(progressValue);
        
        // Stop recording if time limit reached
        if (timeElapsed >= recordingTime) {
          stopRecording();
        }
      }, 1000);
      
      // Handle when user stops sharing screen
      screenStream.getVideoTracks()[0].onended = () => {
        stopRecording();
      };
      
    } catch (err) {
      console.error('Error starting recording:', err);
      alert('Could not start recording. Please make sure to grant screen sharing permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      setRecording(false);
      clearInterval(timerRef.current);
    }
  };

  const downloadRecording = () => {
    if (recordedVideo) {
      const a = document.createElement('a');
      a.href = recordedVideo.url;
      a.download = `screen-recording-${new Date().toISOString().slice(0, 19)}.webm`;
      a.click();
    }
  };

  const uploadRecording = async () => {
    if (!recordedVideo) return;
    
    try {
      const formData = new FormData();
      formData.append('video', recordedVideo.blob, `recording-${Date.now()}.webm`);
      
      const response = await fetch('http://localhost:5000/api/recordings', {
        method: 'POST',
        body: formData
      });
      
      if (response.ok) {
        alert('Recording uploaded successfully!');
      } else {
        alert('Failed to upload recording.');
      }
    } catch (error) {
      console.error('Error uploading recording:', error);
      alert('Error uploading recording.');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="glass-card p-6">
      <h2 className="text-2xl font-semibold mb-6 flex items-center">
        <i className="fas fa-record-vinyl mr-3 text-red-500"></i>
        Record Your Screen
      </h2>
      
      <div className="mb-6">
        <div className="flex justify-between items-center mb-2">
          <label className="text-lg">Recording Length</label>
          <span id="time-display" className="font-mono">
            {formatTime(recordingTime)}
          </span>
        </div>
        <input 
          type="range" 
          min="30" 
          max="180" 
          value={recordingTime} 
          onChange={(e) => setRecordingTime(parseInt(e.target.value))}
          className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer" 
        />
        <div className="flex justify-between text-sm mt-1">
          <span>30s</span>
          <span>3min</span>
        </div>
      </div>
      
      <div className="mb-6">
        <label className="flex items-center cursor-pointer">
          <div className="relative">
            <input 
              type="checkbox" 
              checked={includeAudio}
              onChange={() => setIncludeAudio(!includeAudio)}
              className="sr-only" 
            />
            <div className={`block w-14 h-8 rounded-full ${includeAudio ? 'bg-blue-500' : 'bg-gray-600'}`}></div>
            <div className={`dot absolute left-1 top-1 bg-white w-6 h-6 rounded-full transition transform ${includeAudio ? 'translate-x-6' : ''}`}></div>
          </div>
          <div className="ml-3 text-lg">Include Microphone Audio</div>
        </label>
      </div>
      
      <div className="flex space-x-4 mb-6">
        <button 
          onClick={startRecording} 
          disabled={recording}
          className={`py-3 px-6 rounded-lg flex items-center ${recording ? 'bg-gray-500 opacity-50 cursor-not-allowed' : 'bg-blue-500 hover:bg-blue-600'}`}
        >
          <i className="fas fa-circle mr-2"></i> Start Recording
        </button>
        <button 
          onClick={stopRecording} 
          disabled={!recording}
          className={`py-3 px-6 rounded-lg flex items-center ${!recording ? 'bg-gray-500 opacity-50 cursor-not-allowed' : 'bg-red-500 hover:bg-red-600'}`}
        >
          <i className="fas fa-stop mr-2"></i> Stop Recording
        </button>
      </div>
      
      <div className="relative h-4 bg-gray-700 rounded-full overflow-hidden mb-6">
        <div 
          className="h-full bg-red-500 transition-all duration-300" 
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      
      <div className="text-center">
        <div className={`inline-block px-4 py-2 rounded-full ${recording ? 'bg-red-500 animate-pulse' : 'bg-gray-700'}`}>
          {recording ? `Recording... ${formatTime(timer)}` : recordedVideo ? 'Recording complete' : 'Ready to record'}
        </div>
      </div>
      
      {recordedVideo && (
        <div className="mt-6">
          <h3 className="text-xl font-semibold mb-3">Preview</h3>
          <video 
            ref={videoRef}
            src={recordedVideo.url} 
            controls 
            className="w-full rounded-lg mb-4"
          />
          
          <div className="grid grid-cols-2 gap-4">
            <button 
              onClick={downloadRecording}
              className="py-3 px-4 rounded-lg bg-green-500 hover:bg-green-600 flex items-center justify-center"
            >
              <i className="fas fa-download mr-2"></i> Download
            </button>
            <button 
              onClick={uploadRecording}
              className="py-3 px-4 rounded-lg bg-blue-500 hover:bg-blue-600 flex items-center justify-center"
            >
              <i className="fas fa-cloud-upload-alt mr-2"></i> Upload
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RecordScreen;
