import React, { useState, useEffect } from 'react';

const RecordingsList = () => {
  const [recordings, setRecordings] = useState([]);
  const [activeTab, setActiveTab] = useState('my-recordings');

  useEffect(() => {
    // Fetch recordings from backend
    fetchRecordings();
  }, []);

  const fetchRecordings = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/recordings');
      if (response.ok) {
        const data = await response.json();
        setRecordings(data);
      } else {
        console.error('Failed to fetch recordings');
      }
    } catch (error) {
      console.error('Error fetching recordings:', error);
    }
  };

  const deleteRecording = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/recordings/${id}`, {
        method: 'DELETE'
      });
      
      if (response.ok) {
        // Remove from local state
        setRecordings(recordings.filter(rec => rec.id !== id));
      } else {
        alert('Failed to delete recording');
      }
    } catch (error) {
      console.error('Error deleting recording:', error);
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  return (
    <div className="glass-card p-6 mt-8">
      <div className="flex border-b border-gray-700 mb-6">
        <button 
          className={`tab-button py-3 px-6 ${activeTab === 'my-recordings' ? 'active' : ''}`}
          onClick={() => setActiveTab('my-recordings')}
        >
          My Recordings
        </button>
        <button 
          className={`tab-button py-3 px-6 ${activeTab === 'uploaded' ? 'active' : ''}`}
          onClick={() => setActiveTab('uploaded')}
        >
          Uploaded
        </button>
      </div>
      
      <h2 className="text-2xl font-semibold mb-6 flex items-center">
        <i className="fas fa-history mr-3 text-blue-500"></i>
        Previous Recordings
      </h2>
      
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="text-left border-b border-gray-700">
              <th className="pb-3">Name</th>
              <th className="pb-3">Date</th>
              <th className="pb-3">Size</th>
              <th className="pb-3">Duration</th>
              <th className="pb-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {recordings.length > 0 ? (
              recordings.map(recording => (
                <tr key={recording.id} className="border-b border-gray-700">
                  <td className="py-4">
                    <i className="fas fa-video mr-2 opacity-70"></i>
                    {recording.filename}
                  </td>
                  <td className="py-4">{formatDate(recording.createdAt)}</td>
                  <td className="py-4">{formatFileSize(recording.filesize)}</td>
                  <td className="py-4">--:--</td>
                  <td className="py-4 text-right">
                    <a 
                      href={`http://localhost:5000/api/recordings/${recording.id}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="p-2 text-blue-400 hover:text-blue-300 inline-block"
                    >
                      <i className="fas fa-play"></i>
                    </a>
                    <a 
                      href={`http://localhost:5000/api/recordings/${recording.id}/download`}
                      className="p-2 text-green-400 hover:text-green-300 inline-block"
                    >
                      <i className="fas fa-download"></i>
                    </a>
                    <button 
                      onClick={() => deleteRecording(recording.id)}
                      className="p-2 text-red-400 hover:text-red-300"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="py-8 text-center">
                  <i className="fas fa-inbox text-4xl opacity-50 mb-3"></i>
                  <p>No recordings yet. Start recording to see them here.</p>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default RecordingsList;
